import React from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, CardActions, Button } from '@mui/material';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const courses = [
  { id: 1, title: 'React for Beginners', description: 'Learn the basics of React', image: 'react-course.jpg' },
  { id: 2, title: 'Advanced JavaScript', description: 'Deep dive into JavaScript', image: 'js-course.jpg' },
  { id: 3, title: 'Python for Data Science', description: 'Explore Python and its applications in data science', image: 'python-course.jpg' },
  { id: 4, title: 'UI/UX Design Principles', description: 'Understand the fundamentals of UI/UX design', image: 'design-course.jpg' },
  { id: 5, title: 'Machine Learning Basics', description: 'Get started with machine learning concepts', image: 'ml-course.jpg' },
  { id: 6, title: 'Digital Marketing', description: 'Learn strategies for effective digital marketing', image: 'marketing-course.jpg' },
  { id: 7, title: 'Cloud Computing with AWS', description: 'Introduction to AWS cloud services', image: 'aws-course.jpg' },
  { id: 8, title: 'Cybersecurity Essentials', description: 'Basics of cybersecurity and best practices', image: 'cybersecurity-course.jpg' },
  { id: 9, title: 'Project Management', description: 'Learn project management methodologies', image: 'project-course.jpg' },
  { id: 10, title: 'Data Visualization with Tableau', description: 'Visualize data using Tableau', image: 'tableau-course.jpg' },
];

const Courses = () => {
  return (
    <Container sx={{ marginTop: 4 }}>
      <Typography variant="h3" gutterBottom sx={{ textAlign: 'center' }}>Our Courses</Typography>
      <Grid container spacing={4}>
        {courses.map((course) => (
          <Grid item key={course.id} xs={12} sm={6} md={4}>
            <motion.div whileHover={{ scale: 1.05 }}>
              <Card sx={{ boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)' }}>
                <CardMedia
                  component="img"
                  alt={course.title}
                  height="140"
                  image={course.image}
                />
                <CardContent>
                  <Typography variant="h5" component="div">{course.title}</Typography>
                  <Typography variant="body2" color="text.secondary">{course.description}</Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary" component={Link} to={`/courses/${course.id}`}>Get Started</Button>
                </CardActions>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Courses;
