import React from 'react';
import { Container, Typography, Box, Button, Paper } from '@mui/material';
import { useParams } from 'react-router-dom';

// Mock course data
const courses = [
  { id: 1, title: 'React for Beginners', description: 'Learn the basics of React', image: 'react-course.jpg', details: 'Detailed information about React for Beginners.' },
  { id: 2, title: 'Advanced JavaScript', description: 'Deep dive into JavaScript', image: 'js-course.jpg', details: 'Detailed information about Advanced JavaScript.' },
  { id: 3, title: 'Python for Data Science', description: 'Explore Python and its applications in data science', image: 'python-course.jpg', details: 'Detailed information about Python for Data Science.' },
  { id: 4, title: 'UI/UX Design Principles', description: 'Understand the fundamentals of UI/UX design', image: 'design-course.jpg', details: 'Detailed information about UI/UX Design Principles.' },
  { id: 5, title: 'Machine Learning Basics', description: 'Get started with machine learning concepts', image: 'ml-course.jpg', details: 'Detailed information about Machine Learning Basics.' },
  { id: 6, title: 'Digital Marketing', description: 'Learn strategies for effective digital marketing', image: 'marketing-course.jpg', details: 'Detailed information about Digital Marketing.' },
  { id: 7, title: 'Cloud Computing with AWS', description: 'Introduction to AWS cloud services', image: 'aws-course.jpg', details: 'Detailed information about Cloud Computing with AWS.' },
  { id: 8, title: 'Cybersecurity Essentials', description: 'Basics of cybersecurity and best practices', image: 'cybersecurity-course.jpg', details: 'Detailed information about Cybersecurity Essentials.' },
  { id: 9, title: 'Project Management', description: 'Learn project management methodologies', image: 'project-course.jpg', details: 'Detailed information about Project Management.' },
  { id: 10, title: 'Data Visualization with Tableau', description: 'Visualize data using Tableau', image: 'tableau-course.jpg', details: 'Detailed information about Data Visualization with Tableau.' },
];

const CourseDetail = () => {
  const { courseId } = useParams();
  const course = courses.find((c) => c.id === parseInt(courseId));

  if (!course) {
    return <Typography variant="h6">Course not found</Typography>;
  }

  return (
    <Container sx={{ marginTop: 4 }}>
      <Paper elevation={3} sx={{ padding: 3, textAlign: 'center' }}>
        <Typography variant="h3" gutterBottom>{course.title}</Typography>
        <img src={course.image} alt={course.title} style={{ width: '100%', height: 'auto', borderRadius: 8, marginBottom: 20 }} />
        <Typography variant="h6" gutterBottom>{course.description}</Typography>
        <Typography variant="body1" paragraph>{course.details}</Typography>
        <Button variant="contained" color="primary" size="large" href="#">Get Started</Button>
      </Paper>
    </Container>
  );
};

export default CourseDetail;
