import React from 'react';
import { Container, Typography, Button, Grid, Paper, Avatar, Box } from '@mui/material';
import { School, Code, Public } from '@mui/icons-material';
import backgroundImage from '../assets/background.jpg'; // Add a background image to the src/assets folder

const features = [
  { icon: <School fontSize="large" />, title: 'Quality Education', description: 'Learn from industry experts.' },
  { icon: <Code fontSize="large" />, title: 'Hands-on Learning', description: 'Get practical experience.' },
  { icon: <Public fontSize="large" />, title: 'Global Community', description: 'Join learners from around the world.' },
];

const testimonials = [
  { name: 'Jane Doe', feedback: 'This platform has transformed my career!' },
  { name: 'John Smith', feedback: 'The courses are well-structured and easy to follow.' },
  // Add more testimonials as needed
];

const Home = () => {
  return (
    <>
      <Box
        sx={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          color: 'white',
          padding: '100px 0',
          textAlign: 'center',
        }}
      >
        <Container>
          <Typography variant="h2" gutterBottom>Welcome to Our E-learning Platform</Typography>
          <Typography variant="h5" gutterBottom>Learn at your own pace with our comprehensive courses.</Typography>
          <Button variant="contained" color="primary" size="large" href="/courses">Browse Courses</Button>
        </Container>
      </Box>
      <Container>
        <Grid container spacing={4} sx={{ marginTop: 4 }}>
          {features.map((feature, index) => (
            <Grid item key={index} xs={12} sm={4}>
              <Paper elevation={3} sx={{ padding: 3, textAlign: 'center' }}>
                <Avatar sx={{ bgcolor: 'primary.main', margin: '0 auto', marginBottom: 2 }}>
                  {feature.icon}
                </Avatar>
                <Typography variant="h6" gutterBottom>{feature.title}</Typography>
                <Typography variant="body1">{feature.description}</Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
        <Typography variant="h4" gutterBottom sx={{ marginTop: 8, textAlign: 'center' }}>What Our Learners Say</Typography>
        <Grid container spacing={4} sx={{ marginTop: 2 }}>
          {testimonials.map((testimonial, index) => (
            <Grid item key={index} xs={12} sm={6}>
              <Paper elevation={1} sx={{ padding: 3 }}>
                <Typography variant="body1" paragraph>{testimonial.feedback}</Typography>
                <Typography variant="subtitle1" align="right">- {testimonial.name}</Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
};

export default Home;
