import React from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, CardActions, Button,Box  } from '@mui/material';
import { motion } from 'framer-motion';
const courses = [
  { id: 1, title: 'React for Beginners', description: 'Learn the basics of React', image: 'react-course.jpg', url: 'https://v2.scrimba.com/learn-react-c0e' },
  { id: 2, title: 'Advanced JavaScript', description: 'Deep dive into JavaScript', image: 'js-course.jpg', url: 'https://learnjavascript.online/app.html' },
  { id: 3, title: 'Python for Data Science', description: 'Explore Python and its applications in data science', image: 'python-course.jpg', url: 'https://cognitiveclass.ai/courses/python-for-data-science' },
  { id: 4, title: 'UI/UX Design Principles', description: 'Understand the fundamentals of UI/UX design', image: 'design-course.jpg', url: 'https://cognitiveclass.ai/courses/python-for-data-science' },
  { id: 5, title: 'Machine Learning Basics', description: 'Get started with machine learning concepts', image: 'ml-course.jpg', url: 'https://www.freecodecamp.org/learn/machine-learning-with-python/' },
  { id: 6, title: 'Digital Marketing', description: 'Learn strategies for effective digital marketing', image: 'marketing-course.jpg', url: 'https://www.oxfordhomestudy.com/courses/digital-marketing-certificate-programs-online/digital-marketing-short-course-online#' },
  { id: 7, title: 'Cloud Computing with AWS', description: 'Introduction to AWS cloud services', image: 'aws-course.jpg', url: 'https://www.mygreatlearning.com/academy/learn-for-free/courses/cloud-foundations' },
  { id: 8, title: 'Cybersecurity Essentials', description: 'Basics of cybersecurity and best practices', image: 'cybersecurity-course.jpg', url: 'https://www.classcentral.com/course/swayam-introduction-to-cyber-security-14116' },
  { id: 9, title: 'Project Management', description: 'Learn project management methodologies', image: 'project-course.jpg', url: 'https://alison.com/course/diploma-in-project-management-revised-2017' },
  { id: 10, title: 'Data Visualization with Tableau', description: 'Visualize data using Tableau', image: 'tableau-course.jpg', url: 'https://www.simplilearn.com/free-tableau-course-skillup' },
];
const backgroundImg = require('../assets/image.webp');
const Courses = () => {
  return (
    <Box
      sx={{
        backgroundImage: require('../assets/image.webp'), // Replace with your background image URL
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '2rem 0',
      }}
    >
    <Container sx={{ marginTop: 4 }}>
      <Typography variant="h3" gutterBottom sx={{ textAlign: 'center' }}>Courses</Typography>
      <Grid container spacing={4}>
        {courses.map((course) => (
          <Grid item key={course.id} xs={12} sm={6} md={4}>
            <motion.div whileHover={{ scale: 1.05 }}>
              <Card sx={{ boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)' }}>
                <CardMedia
                  component="img"
                  alt={course.title}
                  height="140"
                  image={course.image}
                />
                <CardContent>
                  <Typography variant="h5" component="div">{course.title}</Typography>
                  <Typography variant="body2" color="text.secondary">{course.description}</Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary" href={course.url} target="_blank" rel="noopener noreferrer">Get Started</Button>
                </CardActions>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
    </Box>
  );
};

export default Courses;
