import React from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, CardActions, Button } from '@mui/material';

const courses = [
  { id: 1, title: 'React for Beginners', description: 'Learn the basics of React', image: 'react-course.jpg' },
  { id: 2, title: 'Advanced JavaScript', description: 'Deep dive into JavaScript', image: 'js-course.jpg' },
  // Add more courses as needed
];

const Courses = () => {
  return (
    <Container>
      <Typography variant="h2" gutterBottom>Our Courses</Typography>
      <Grid container spacing={4}>
        {courses.map((course) => (
          <Grid item key={course.id} xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                alt={course.title}
                height="140"
                image={course.image}
              />
              <CardContent>
                <Typography variant="h5" component="div">{course.title}</Typography>
                <Typography variant="body2" color="text.secondary">{course.description}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary">Enroll Now</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Courses;
